mapper.select:
select o.id, o.name, o.description, o.created_time as createdTime, o.updated_time as updatedTime, o.capacity,ou.organization_role as organizationRole,(select count(*) from organization_user ou2 where ou2.organization_id = o.id) as userCount, (select count(*) from organization_file orf where orf.organization_id = o.id) as fileCount
from organization_user ou, organization o

    ou.organization_id = o.id
    and ou.user_id = #{userId}

        and ou.organization_role = #{organizationRole}
